INSERT INTO `post_info` VALUES (295, '小米Go开发一面面经', '<div class=\"post-topic-des nc-post-content js-nc-pop-image\">\r\n                    <p>1、实习经历</p> \r\n<p>2、项目经历</p> \r\n<p>3、golang gc过程</p> \r\n<p>4、go slice底层原理</p> \r\n<p>5、new和make的区别</p> \r\n<p>6、channel底层</p> \r\n<p>7、两个协程交替打印一个数组，使数组中的数据按顺序输出</p> \r\n<p>8、同步/阻塞/异步</p> \r\n<p>9、Redis数据类型和应用场景</p> \r\n<p>10、Redis做缓存时可能出现什么问题，缓存击穿、缓存穿透的解决办法</p> \r\n<p>11、数据库隔离级别，可重复读的实现原理</p> \r\n<p>12、mysql索引</p> \r\n<p>13、TCP4次挥手，time_wait是什么时候开始的，怎么解决大量time_wait存在的情况</p> \r\n<p><span>14、<a class=\"content-link  js-post-content-keyword\" href=\"/jump/super-jump/word?word=%E7%AE%97%E6%B3%95\" target=\"_blank\">算法</a>：<a class=\"content-link  js-post-content-keyword\" href=\"/jump/super-jump/word?word=%E8%BF%9E%E7%BB%AD%E5%AD%90%E6%95%B0%E7%BB%84%E7%9A%84%E6%9C%80%E5%A4%A7%E5%92%8C\" target=\"_blank\">连续子数组的最大和</a></span></p> \r\n<p>15、反问</p>\r\n                          </div>', 1, 3, '2022-05-02 10:36:58', '2022-05-02 11:30:51', 1, 0, 0, 1, 0, 0, 0, '2022-05-02 10:36:58');
